import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login, signup, adminLogin } from "../services/authService";
import "../styles/SignupLogin.css";

export default function SignupLogin({ setIsAdmin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [isAdminLogin, setIsAdminLogin] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
  });
  const [forgotPassword, setForgotPassword] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  
  const navigate = useNavigate();

  // Handle input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    setError(""); // Clear error when user types
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      // Forgot Password Flow
      if (forgotPassword) {
        alert("Password reset link sent to your email!");
        setForgotPassword(false);
        setLoading(false);
        return;
      }

      // Admin Login Flow
      if (isAdminLogin) {
        const response = await adminLogin(formData.email, formData.password);
        
        if (response.token) {
          alert("Admin login successful!");
          setIsAdmin(true);
          navigate("/dashboard");
        }
        setLoading(false);
        return;
      }

      // Regular User Login
      if (isLogin) {
        const response = await login(formData.email, formData.password);
        
        if (response.success) {
          alert(`Welcome back, ${response.user.name || response.user.email}!`);
          navigate("/");
        }
      } 
      // User Signup
      else {
        const response = await signup(
          formData.email, 
          formData.password, 
          formData.name
        );
        
        if (response.success) {
          alert("Signup successful! Please login.");
          setIsLogin(true);
          setFormData({ email: formData.email, password: "", name: "" });
        }
      }
    } catch (err) {
      console.error("Auth error:", err);
      setError(err.message || "Something went wrong!");
    } finally {
      setLoading(false);
    }
  };

  // Toggle between login/signup
  const toggleMode = () => {
    setIsLogin(!isLogin);
    setIsAdminLogin(false);
    setForgotPassword(false);
    setError("");
    setFormData({ email: "", password: "", name: "" });
  };

  // Toggle admin login
  const toggleAdminLogin = () => {
    setIsAdminLogin(!isAdminLogin);
    setIsLogin(true);
    setForgotPassword(false);
    setError("");
    setFormData({ email: "", password: "", name: "" });
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        {/* Header */}
        <h2>
          {forgotPassword
            ? "Forgot Password"
            : isAdminLogin
            ? "Admin Login üë®‚Äçüíº"
            : isLogin
            ? "Welcome Back üçé"
            : "Join Appleverse üçè"}
        </h2>

        {/* Error Message */}
        {error && <div className="error-message">{error}</div>}

        {/* Form */}
        <form onSubmit={handleSubmit}>
          {/* Name field (only for signup) */}
          {!isLogin && !forgotPassword && !isAdminLogin && (
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={formData.name}
              onChange={handleChange}
            />
          )}

          {/* Email field */}
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            value={formData.email}
            onChange={handleChange}
            required
          />

          {/* Password field */}
          {!forgotPassword && (
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              minLength={6}
            />
          )}

          {/* Submit Button */}
          <button type="submit" className="auth-btn" disabled={loading}>
            {loading
              ? "Processing..."
              : forgotPassword
              ? "Send Reset Link"
              : isAdminLogin
              ? "Admin Login"
              : isLogin
              ? "Login"
              : "Sign Up"}
          </button>
        </form>

        {/* Links */}
        <div className="auth-links">
          {/* Forgot Password Link */}
          {!forgotPassword && isLogin && !isAdminLogin && (
            <p onClick={() => setForgotPassword(true)} className="link">
              Forgot Password?
            </p>
          )}

          {/* Back to Login */}
          {forgotPassword && (
            <p onClick={() => setForgotPassword(false)} className="link">
              Back to Login
            </p>
          )}

          {/* Toggle Login/Signup */}
          {!forgotPassword && !isAdminLogin && (
            <p>
              {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
              <span className="link" onClick={toggleMode}>
                {isLogin ? "Sign Up" : "Login"}
              </span>
            </p>
          )}

          {/* Admin Login Toggle */}
          {!forgotPassword && (
            <p className="admin-toggle">
              {isAdminLogin ? (
                <span className="link" onClick={toggleAdminLogin}>
                  ‚Üê Regular Login
                </span>
              ) : (
                <span className="link" onClick={toggleAdminLogin}>
                  Admin Login ‚Üí
                </span>
              )}
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
