import React, { useState, useEffect } from 'react';
import { Apple, Home, Package, Info, LogIn, PlusCircle, LayoutDashboard, User } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { getCurrentUser, isAuthenticated, logout } from '../services/authService';

const Navigation = () => {
  const [activeTab, setActiveTab] = useState('home');
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdminUser, setIsAdminUser] = useState(false);
  const navigate = useNavigate();

  // Check if user is logged in on component mount
  useEffect(() => {
    const currentUser = getCurrentUser();
    const loggedIn = isAuthenticated();
    const adminStatus = localStorage.getItem('isAdmin') === 'true';
    
    setUser(currentUser);
    setIsLoggedIn(loggedIn);
    setIsAdminUser(adminStatus);
  }, []);

  const tabs = [
    { id: 'home', label: 'Home', icon: Home, route: '/' },
    { id: 'library', label: 'Library', icon: Package, route: '/library' },
    { id: 'blogs', label: 'Blogs', icon: PlusCircle, route: '/blogs' },
    { id: 'about', label: 'About', icon: Info, route: '/about' },
  ];

  // Add dashboard tab based on user type
  if (isAdminUser) {
    tabs.push({ id: 'dashboard', label: 'Dashboard', icon: LayoutDashboard, route: '/dashboard' });
  } else if (isLoggedIn) {
    tabs.push({ id: 'dashboard', label: 'My Dashboard', icon: User, route: '/user-dashboard' });
  }

  const handleTabClick = (tab) => {
    setActiveTab(tab.id);
    if (tab.route) {
      navigate(tab.route);
    }
  };

  const handleSignupLogin = () => {
    navigate('/signup-login');
  };

  const handleLogout = () => {
    logout();
    setUser(null);
    setIsLoggedIn(false);
    setIsAdminUser(false);
    navigate('/');
  };

  return (
    <nav className="navigation">
      <div className="nav-container">
        {/* Logo/Brand */}
        <div className="nav-brand">
          <Apple size={28} className="brand-icon" />
          <span className="brand-text">AppleVerse 2.0</span>
        </div>

        {/* Main Navigation Tabs */}
        <div className="nav-tabs">
          {tabs.map((tab) => {
            const IconComponent = tab.icon;
            return (
              <button
                key={tab.id}
                className={`nav-tab ${activeTab === tab.id ? 'active' : ''}`}
                onClick={() => handleTabClick(tab)}
              >
                <IconComponent size={18} />
                <span>{tab.label}</span>
              </button>
            );
          })}
        </div>

        {/* User Actions */}
        <div className="nav-actions">
          {isLoggedIn ? (
            <div className="user-menu">
              <span className="user-greeting">
                Hi, {user?.name || user?.email}
              </span>
              <button
                className="logout-btn"
                onClick={handleLogout}
                title="Logout"
              >
                <LogIn size={18} />
                <span>Logout</span>
              </button>
            </div>
          ) : (
            <button
              className="admin-login-btn"
              onClick={handleSignupLogin}
              title="Signup or Login"
            >
              <LogIn size={18} />
              <span>Signup/Login</span>
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
